import React, { useEffect, useState } from 'react';

const BudgetPlan = ({ onBalanceChange, onAllowanceChange, expenses, setExpenses }) => {
  const [incomes, setIncomes] = useState([{ id: 1, amount: 0 }]);
  const [mustHaves, setMustHaves] = useState([
    { name: 'Student Loans', amount: 120 },
    { name: 'Phone Bill', amount: 40 },
    { name: 'Medicine', amount: 30 },
  ]);
  const [goalAllocations, setGoalAllocations] = useState([
    { name: 'Car Downpayment', amount: 10 },
    { name: 'Phone', amount: 12 },
  ]);
  const [minSavings, setMinSavings] = useState(100);
  const [newExpense, setNewExpense] = useState({ category: '', amount: '' });

  // ⏪ Load saved data on mount
  useEffect(() => {
    const savedIncomes = JSON.parse(localStorage.getItem('incomes'));
    const savedMustHaves = JSON.parse(localStorage.getItem('mustHaves'));
    const savedGoals = JSON.parse(localStorage.getItem('goalAllocations'));
    const savedSavings = localStorage.getItem('minSavings');

    if (savedIncomes) setIncomes(savedIncomes);
    if (savedMustHaves) setMustHaves(savedMustHaves);
    if (savedGoals) setGoalAllocations(savedGoals);
    if (savedSavings !== null) setMinSavings(parseFloat(savedSavings));
  }, []);

  // Calculate totals
  const totalIncome = incomes.reduce((sum, inc) => sum + (parseFloat(inc.amount) || 0), 0);
  const mustHaveTotal = mustHaves.reduce((sum, item) => sum + (parseFloat(item.amount) || 0), 0);
  const goalTotal = goalAllocations.reduce((sum, item) => sum + (parseFloat(item.amount) || 0), 0);
  const allowance = totalIncome - mustHaveTotal - goalTotal - minSavings;

  // ⏫ Sync balance and allowance with App
  useEffect(() => {
    onBalanceChange(totalIncome);
    onAllowanceChange(allowance);
  }, [totalIncome, mustHaveTotal, goalTotal, minSavings]);

  // 💾 Save all budget data to localStorage
  useEffect(() => {
    localStorage.setItem('incomes', JSON.stringify(incomes));
    localStorage.setItem('mustHaves', JSON.stringify(mustHaves));
    localStorage.setItem('goalAllocations', JSON.stringify(goalAllocations));
    localStorage.setItem('minSavings', minSavings);
  }, [incomes, mustHaves, goalAllocations, minSavings]);

  const handleIncomeChange = (index, value) => {
    const updated = [...incomes];
    updated[index].amount = value;
    setIncomes(updated);
  };

  const addIncomeField = () => {
    setIncomes([...incomes, { id: Date.now(), amount: 0 }]);
  };

  const handleMustHaveChange = (index, value) => {
    const updated = [...mustHaves];
    updated[index].amount = value;
    setMustHaves(updated);
  };

  const handleGoalChange = (index, value) => {
    const updated = [...goalAllocations];
    updated[index].amount = value;
    setGoalAllocations(updated);
  };

  const addTransaction = () => {
    if (!newExpense.category || !newExpense.amount) return;
    const entry = {
      id: Date.now(),
      category: newExpense.category,
      amount: parseFloat(newExpense.amount),
    };
    setExpenses([entry, ...expenses]); // latest on top
    setNewExpense({ category: '', amount: '' });
  };

  return (
    <div className="p-6 max-w-3xl mx-auto space-y-6">
      <h2 className="text-3xl font-bold text-center">📊 Monthly Budget Planner</h2>

      {/* Income Inputs */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">💼 Income Sources</h3>
        {incomes.map((inc, idx) => (
          <input
            key={inc.id}
            type="number"
            placeholder={`Income #${idx + 1}`}
            value={inc.amount}
            onChange={(e) => handleIncomeChange(idx, e.target.value)}
            className="w-full border p-2 mb-2 rounded"
          />
        ))}
        <button
          onClick={addIncomeField}
          className="mt-2 text-blue-600 underline"
        >
          + Add Another Income Source
        </button>
      </div>

      {/* Must-Haves */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">🧾 Must-Have Expenses</h3>
        {mustHaves.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => handleMustHaveChange(idx, e.target.value)}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      {/* Goals */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">🎯 Goal Allocations</h3>
        {goalAllocations.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => handleGoalChange(idx, e.target.value)}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      {/* Savings */}
      <div className="bg-white p-4 shadow rounded">
        <label className="block mb-2 font-semibold">💰 Minimum Savings</label>
        <input
          type="number"
          value={minSavings}
          onChange={(e) => setMinSavings(parseFloat(e.target.value))}
          className="w-full border p-2 rounded"
        />
      </div>

      {/* Final Allowance Display */}
      <div className="bg-green-50 p-6 shadow rounded text-center">
        <h3 className="text-xl font-semibold mb-2">🧮 Suggested Monthly Spending Allowance</h3>
        <p className="text-4xl font-bold text-green-800">
          ${allowance > 0 ? allowance.toFixed(2) : '0.00'}
        </p>
        {allowance < 0 && (
          <p className="text-red-600 mt-2">⚠️ Over budget. Reduce expenses or earn more.</p>
        )}
      </div>

      {/* Add Transaction Section */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">➕ Add Transaction</h3>
        <input
          type="text"
          placeholder="Category"
          value={newExpense.category}
          onChange={(e) => setNewExpense({ ...newExpense, category: e.target.value })}
          className="border p-2 mr-2 rounded"
        />
        <input
          type="number"
          placeholder="Amount"
          value={newExpense.amount}
          onChange={(e) => setNewExpense({ ...newExpense, amount: e.target.value })}
          className="border p-2 mr-2 rounded"
        />
        <button
          onClick={addTransaction}
          className="bg-green-500 text-white px-4 py-2 rounded"
        >
          Add
        </button>
      </div>
    </div>
  );
};

export default BudgetPlan;
