import React from 'react';
import { Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const HomeScreen = ({ balance, expenses, allowance }) => {
  const categories = [...new Set(expenses.map(e => e.category))];

  const pieData = {
    labels: categories,
    datasets: [{
      data: categories.map(cat =>
        expenses.filter(e => e.category === cat)
                .reduce((sum, e) => sum + e.amount, 0)
      ),
      backgroundColor: ['#cfe3d4', '#ffd8be', '#d6e6f2', '#ffe4e1', '#fce1f0', '#f9f7cf'],
      borderWidth: 1,
    }],
  };

  const barData = {
    labels: ['January', 'February', 'March', 'April'],
    datasets: [{
      label: 'Monthly Savings',
      data: [80, 120, 60, 150], // Placeholder; can later be made dynamic
      backgroundColor: '#91a377',
    }],
  };

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">
      <h1 className="text-3xl font-bold text-center mb-4">Budget Bloom 🌱</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Balance */}
        <div className="bg-white p-6 shadow rounded flex flex-col justify-center items-center h-full">
          <h2 className="text-xl font-semibold mb-2 text-center">💰 Available Balance</h2>
          <p className="text-5xl font-extrabold text-green-800">${balance}</p>
        </div>

        {/* Allowance */}
        <div className="bg-white p-6 shadow rounded flex flex-col justify-center items-center h-full">
          <h2 className="text-xl font-semibold mb-2 text-center">🧾 Suggested Monthly Allowance</h2>
          <p className="text-4xl font-bold text-blue-700">
            ${allowance > 0 ? allowance.toFixed(2) : '0.00'}
          </p>
          {allowance < 0 && (
            <p className="text-red-500 text-sm mt-1 text-center">
              ⚠️ Over budget — consider reducing expenses or increasing income.
            </p>
          )}
        </div>

        {/* Spending by Category */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold mb-2">📊 Spending by Category</h2>
          <Pie data={pieData} />
        </div>

        {/* Monthly Savings */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold mb-2">📈 Monthly Savings</h2>
          <Bar data={barData} />
        </div>

        {/* Recent Activity */}
        <div className="bg-white p-4 shadow rounded col-span-full">
          <h2 className="text-lg font-semibold mb-2">📝 Recent Activity</h2>
          <ul className="list-disc list-inside max-h-48 overflow-y-auto">
            {[...expenses].reverse().map(e => (
              <li key={e.id}>{e.category}: ${e.amount}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default HomeScreen;
