import React from 'react';
import { Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Dashboard = ({ balance, expenses, allowance }) => {
  const categories = [...new Set(expenses.map(e => e.category))];

  const pieData = {
    labels: categories,
    datasets: [{
      data: categories.map(cat =>
        expenses.filter(e => e.category === cat)
                .reduce((sum, e) => sum + e.amount, 0)
      ),
      backgroundColor: ['#cfe3d4', '#ffd8be', '#d6e6f2', '#ffe4e1', '#fce1f0', '#f9f7cf'],
      borderWidth: 1,
    }],
  };

  const barData = {
    labels: ['January', 'February', 'March', 'April'],
    datasets: [{
      label: 'Monthly Savings',
      data: [80, 120, 60, 150],
      backgroundColor: '#91a377',
    }],
  };

  const totalSpent = expenses.reduce((sum, e) => sum + e.amount, 0);
  const checking = 600;
  const card = 650;
  const savings = 300;
  const investments = 400;
  const netCash = checking + card - totalSpent;

  const today = new Date().toLocaleDateString(undefined, { weekday: 'long', month: 'long', day: 'numeric' });

  return (
    <div className="p-6 max-w-6xl mx-auto space-y-6">
      <div className="flex justify-between items-center">
        <h1
          className="text-4xl"
          style={{ fontFamily: 'Pacifico, cursive', color: '#800000' }}
        >
          Hello, Eshita
        </h1>
        <p className="text-gray-600 font-medium">{today}</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold">🏦 Checking Account</h2>
          <p className="text-3xl font-bold text-green-700">${checking}</p>
        </div>

        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold">💳 Credit/Debit Card</h2>
          <p className="text-3xl font-bold text-indigo-600">${card}</p>
        </div>

        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold">💼 Net Cash</h2>
          <p className="text-3xl font-bold text-emerald-600">${netCash}</p>
        </div>

        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold">💰 Savings</h2>
          <p className="text-3xl font-bold text-yellow-700">${savings}</p>
        </div>

        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold">📈 Investments</h2>
          <p className="text-3xl font-bold text-blue-700">${investments}</p>
        </div>

        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold">🧮 Suggested Allowance</h2>
          <p className="text-3xl font-bold text-purple-700">
            ${allowance > 0 ? allowance.toFixed(2) : '0.00'}
          </p>
        </div>
      </div>

      {/* Pie & Bar Charts */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold mb-2">📊 Spending by Category</h2>
          <Pie data={pieData} />
        </div>

        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold mb-2">📈 Monthly Savings</h2>
          <Bar data={barData} />
        </div>
      </div>

      {/* Recent Activity */}
      <div className="bg-white p-4 shadow rounded">
        <h2 className="text-lg font-semibold mb-2">📝 Recent Activity</h2>
        <ul className="list-disc list-inside max-h-48 overflow-y-auto">
          {[...expenses].reverse().map(e => (
            <li key={e.id}>{e.category}: ${e.amount}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
