import React from 'react';
import { Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const HomeScreen = ({ balance, expenses, allowance }) => {
  const categories = [...new Set(expenses.map(e => e.category))];

  const pieData = {
    labels: categories,
    datasets: [{
      data: categories.map(cat =>
        expenses.filter(e => e.category === cat)
                .reduce((sum, e) => sum + e.amount, 0)
      ),
      backgroundColor: ['#cfe3d4', '#ffd8be', '#d6e6f2', '#ffe4e1', '#fce1f0', '#f9f7cf'],
      borderWidth: 1,
    }],
  };

  const barData = {
    labels: ['January', 'February', 'March', 'April'],
    datasets: [{
      label: 'Monthly Savings',
      data: [80, 120, 60, 150],
      backgroundColor: '#91a377',
    }],
  };

  const today = new Date().toLocaleDateString();

  return (
    <div className="p-6 max-w-6xl mx-auto space-y-8">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h1 className="text-4xl font-bold" style={{ fontFamily: 'Pacifico', color: '#800000' }}>
          Hello, Eshita üëã
        </h1>
        <p className="text-lg text-gray-700">{today}</p>
      </div>

      {/* Financial Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white p-6 shadow rounded">
          <h2 className="text-lg font-semibold text-center mb-2">üè¶ Checking Account</h2>
          <p className="text-3xl text-center font-bold text-green-700">${(balance * 0.6).toFixed(2)}</p>
        </div>
        <div className="bg-white p-6 shadow rounded">
          <h2 className="text-lg font-semibold text-center mb-2">üí≥ Credit/Debit Card</h2>
          <p className="text-3xl text-center font-bold text-blue-700">${(balance * 0.3).toFixed(2)}</p>
        </div>
        <div className="bg-white p-6 shadow rounded">
          <h2 className="text-lg font-semibold text-center mb-2">üíº Net Cash</h2>
          <p className="text-3xl text-center font-bold text-purple-700">${balance.toFixed(2)}</p>
        </div>
      </div>

      {/* Savings + Investments */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-6 shadow rounded">
          <h2 className="text-lg font-semibold text-center mb-2">üí∞ Savings</h2>
          <p className="text-3xl text-center font-bold text-indigo-700">${(balance * 0.1).toFixed(2)}</p>
        </div>
        <div className="bg-white p-6 shadow rounded">
          <h2 className="text-lg font-semibold text-center mb-2">üìà Investments</h2>
          <p className="text-3xl text-center font-bold text-rose-700">${(balance * 0.05).toFixed(2)}</p>
        </div>
      </div>

      {/* Budgeting Overview */}
      <div className="bg-green-50 p-6 shadow rounded text-center">
        <h2 className="text-xl font-semibold mb-2">üßæ Suggested Monthly Allowance</h2>
        <p className="text-4xl font-bold text-green-800">
          ${allowance > 0 ? allowance.toFixed(2) : '0.00'}
        </p>
        {allowance < 0 && (
          <p className="text-red-600 mt-2">‚ö†Ô∏è Over budget. Adjust your spending or increase income.</p>
        )}
      </div>

      {/* Graphs */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold mb-2">üìä Spending by Category</h2>
          <Pie data={pieData} />
        </div>
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-lg font-semibold mb-2">üìà Monthly Savings</h2>
          <Bar data={barData} />
        </div>
      </div>

      {/* Recent Transactions */}
      <div className="bg-white p-4 shadow rounded">
        <h2 className="text-lg font-semibold mb-3">üìù Recent Transactions</h2>
        <ul className="list-disc list-inside max-h-64 overflow-y-auto space-y-1">
          {[...expenses].reverse().map(e => (
            <li key={e.id} className="flex justify-between">
              <span>{e.category}: ${e.amount}</span>
              <span className="text-sm text-gray-500">{e.date}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default HomeScreen;
