import React, { useState } from 'react';
import HomeScreen from './components/HomeScreen';
import AddTransaction from './components/AddTransaction';
import SetGoal from './components/SetGoal';
import LearnScreen from './components/LearnScreen';
import NavBar from './components/NavBar';

function App() {
  const [screen, setScreen] = useState('home');
  const [theme, setTheme] = useState('pastel-pink'); // default theme

  return (
    <div className={`min-h-screen bg-${theme}`}>
      {/* Theme Selector UI */}
      <div className="flex justify-end p-2 bg-white">
        <select
          className="p-2 border rounded"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
        >
          <option value="pastel-pink">ðŸŒ¸ Pastel Pink</option>
          <option value="beige">ðŸŸ¤ Beige</option>
          <option value="sage-light">ðŸŒ¿ Sage Light</option>
        </select>
      </div>

      {/* Nav + Screens */}
      <NavBar setScreen={setScreen} />
      {screen === 'home' && <HomeScreen />}
      {screen === 'add' && <AddTransaction />}
      {screen === 'goal' && <SetGoal />}
      {screen === 'learn' && <LearnScreen />}
    </div>
  );

}

export default App;
