import React, { useState, useEffect, useCallback } from 'react';

const months = [
  'January','February','March','April','May','June',
  'July','August','September','October','November','December'
];
const keyFor = (y, m) => `budget-${y}-${m}`;           // e.g. budget-2025-6
const fetchGoals = () => JSON.parse(localStorage.getItem('goals')) || [];

const defaultMustHaves = [
  { name: 'Student Loans', amount: 120 },
  { name: 'Phone Bill',    amount:  40 },
  { name: 'Medicine',      amount:  30 },
];

const BudgetPlan = ({ onBalanceChange, onAllowanceChange }) => {
  /* ---- calendar selectors ---- */
  const now = new Date();
  const [year,  setYear]  = useState(now.getFullYear());
  const [month, setMonth] = useState(now.getMonth());         // 0-based

  /* ---- budget state ---- */
  const [incomes,        setIncomes]         = useState([]);
  const [mustHaves,      setMustHaves]       = useState([]);
  const [goalAllocations,setGoalAllocations] = useState([]);
  const [minSavings,     setMinSavings]      = useState(100);

  /* ---- build starting goal allocations from Goals page ---- */
  const buildGoalRows = (savedRows=[]) => {
    const goals = fetchGoals();           // [{id,name,â€¦}, â€¦] from Goals page
    const map   = Object.fromEntries(savedRows.map(g => [g.name, g]));
    return goals.map(g =>
      map[g.name] ? { ...map[g.name] } : { id: g.id, name: g.name, amount: 0 }
    );
  };

  /* ---- load selected period ---- */
  const loadBudget = useCallback(() => {
    const stored = JSON.parse(localStorage.getItem(keyFor(year, month)));
    if (stored) {
      setIncomes(stored.incomes);
      setMustHaves(stored.mustHaves);
      setGoalAllocations(buildGoalRows(stored.goalAllocations));
      setMinSavings(stored.minSavings);
    } else {
      /* first time for this period */
      setIncomes([{ id: 1, label: 'Income Source', amount: 0 }]);
      setMustHaves(defaultMustHaves);
      setGoalAllocations(buildGoalRows());
      setMinSavings(100);
    }
  }, [year, month]);

  useEffect(loadBudget, [loadBudget]);

  /* ---- persist + recalc whenever anything changes ---- */
  const persistAndRecalc = useCallback(() => {
    const totalIncome = incomes.reduce((s, i) => s + (+i.amount || 0), 0);
    const mustTotal   = mustHaves.reduce((s, m) => s + (+m.amount || 0), 0);
    const goalTotal   = goalAllocations.reduce((s, g) => s + (+g.amount || 0), 0);
    const allowance   = totalIncome - mustTotal - goalTotal - minSavings;

    onBalanceChange(totalIncome);
    onAllowanceChange(allowance);

    localStorage.setItem(
      keyFor(year, month),
      JSON.stringify({ incomes, mustHaves, goalAllocations, minSavings })
    );
  }, [incomes, mustHaves, goalAllocations, minSavings, year, month,
      onBalanceChange, onAllowanceChange]);

  useEffect(persistAndRecalc,
    [incomes, mustHaves, goalAllocations, minSavings, persistAndRecalc]);

  /* manual save */
  const saveAll = () => {
    persistAndRecalc();
    window.alert('ðŸ’¾ Budget saved!');
  };

  /* helpers */
  const addIncomeField = () =>
    setIncomes([...incomes, { id: Date.now(), label: 'Income Source', amount: 0 }]);

  /* ---- UI ---- */
  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">
      <h2 className="text-3xl font-bold text-center">ðŸ“Š Monthly Budget Planner</h2>

      {/* Period Picker */}
      <div className="flex gap-4 justify-center mb-4">
        <select
          className="border p-2 rounded"
          value={year}
          onChange={(e) => setYear(+e.target.value)}
        >
          {[...Array(6)].map((_, i) => {
            const y = now.getFullYear() - 2 + i;
            return <option key={y} value={y}>{y}</option>;
          })}
        </select>

        <select
          className="border p-2 rounded"
          value={month}
          onChange={(e) => setMonth(+e.target.value)}
        >
          {months.map((m, idx) => (
            <option key={m} value={idx}>{m}</option>
          ))}
        </select>
      </div>

      {/* Income Inputs */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">ðŸ’¼ Income Sources</h3>
        {incomes.map((inc, idx) => (
          <input
            key={inc.id}
            type="number"
            placeholder={`Income #${idx + 1}`}
            value={inc.amount}
            onChange={(e) => {
              const updated = [...incomes];
              updated[idx].amount = e.target.value;
              setIncomes(updated);
            }}
            className="w-full border p-2 mb-2 rounded"
          />
        ))}
        <button onClick={addIncomeField} className="mt-2 text-blue-600 underline">
          + Add Another Income
        </button>
      </div>

      {/* Must-Haves */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">ðŸ§¾ Must-Haves</h3>
        {mustHaves.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => {
                const updated = [...mustHaves];
                updated[idx].amount = e.target.value;
                setMustHaves(updated);
              }}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      {/* Goals imported from Goals page */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">ðŸŽ¯ Goals (monthly allocation)</h3>
        {goalAllocations.length === 0 && (
          <p className="text-sm text-gray-500">
            No goals yet â€“ add them on the Goals page.
          </p>
        )}
        {goalAllocations.map((g) => (
          <div key={g.id} className="flex justify-between mb-2">
            <span>{g.name}</span>
            <input
              type="number"
              value={g.amount}
              onChange={(e) => {
                const updated = goalAllocations.map((goal) =>
                  goal.id === g.id ? { ...goal, amount: +e.target.value } : goal
                );
                setGoalAllocations(updated);
              }}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      {/* Savings */}
      <div className="bg-white p-4 shadow rounded">
        <label className="block mb-2 font-semibold">ðŸ’° Minimum Savings</label>
        <input
          type="number"
          value={minSavings}
          onChange={(e) => setMinSavings(+e.target.value)}
          className="w-full border p-2 rounded"
        />
      </div>

      {/* Save Button */}
      <button
        onClick={saveAll}
        className="bg-green-600 text-white px-6 py-2 rounded block mx-auto"
      >
        ðŸ’¾ Save Budget Plan
      </button>
    </div>
  );
};

export default BudgetPlan;
