import React, { useState, useRef } from 'react';

const topics = [
  { id: 1, title: 'Budgeting 💸', detail: 'Budgeting helps you plan your income and expenses so you don’t run out of money. It’s about tracking what you earn vs. what you spend.' },
  { id: 2, title: 'Saving vs Investing 🏦📈', detail: 'Saving is setting money aside safely. Investing is putting money into assets like stocks, hoping they grow over time.' },
  { id: 3, title: 'Credit Scores 💳', detail: 'Your credit score tells lenders how trustworthy you are with borrowed money. Pay on time, keep balances low, and don’t open too many cards at once.' },
  { id: 4, title: 'Bank Accounts 🏛️', detail: 'Checking accounts are for daily spending. Savings accounts help you store money with interest.' },
  { id: 5, title: 'Student Loans 🎓', detail: 'Student loans are borrowed money for school. Check interest rates and repayment plans. Info: https://studentaid.gov' },
  { id: 6, title: 'Taxes & IRS 💼', detail: 'Taxes fund public services. File yearly with the IRS. Visit: https://irs.gov' },
  { id: 7, title: 'Inflation 📉', detail: 'Inflation means prices go up over time. It affects what your money can buy. The Federal Reserve tries to control it.' },
  { id: 8, title: 'Interest Rates 🔁', detail: 'Interest is the cost of borrowing money. High rates = expensive loans. Low rates = cheaper borrowing.' },
  { id: 9, title: 'Retirement: 401(k) & IRA 👴', detail: 'These are savings plans for retirement. Start early. Employers may match contributions in 401(k)s.' },
  { id: 10, title: 'Health & Auto Insurance 🚗🩺', detail: 'Insurance protects you from big expenses in emergencies. Know your deductible and coverage limits.' },
  { id: 11, title: 'Scams & Fraud 🚨', detail: 'Never give out info to strangers. Learn more: https://consumer.ftc.gov/articles/how-avoid-scam' },
  { id: 12, title: 'The Federal Reserve 🏦', detail: 'The Fed manages the U.S. money supply and interest rates. Its decisions affect inflation, jobs, and growth.' },
  { id: 13, title: 'GDP & CPI 📊', detail: 'GDP = total economic output. CPI = how much consumer goods prices change. Both help track economic health.' },
  { id: 14, title: 'Supply & Demand ⚖️', detail: 'Prices go up when demand > supply. Prices fall when supply > demand. This is basic market behavior.' },
  { id: 15, title: 'Stocks vs Bonds 📈🔗', detail: 'Stocks = company ownership. Bonds = lending money to governments/companies with interest returns.' },
  { id: 16, title: 'Mutual Funds & ETFs 🪙', detail: 'These bundle many investments together. Good for diversification with lower risk.' },
  { id: 17, title: 'Emergency Funds 🆘', detail: 'Save 3–6 months of expenses for emergencies. Keep it in a safe, accessible place.' },
  { id: 18, title: 'Minimum Wage & Labor Rights 💼', detail: 'Minimum wage laws protect worker earnings. Know your rights at: https://www.dol.gov/agencies/whd' },
  { id: 19, title: 'Economic Inequality 📉📈', detail: 'This happens when wealth is unevenly distributed. Policies like taxes, benefits, and education can help.' },
  { id: 20, title: 'Government Spending 🏛️', detail: 'Governments spend on defense, healthcare, education, etc. This impacts the economy and your taxes.' },
  { id: 21, title: 'How to Start Investing 📊', detail: 'Start with what you can afford. Use platforms like Vanguard, Fidelity, or Robinhood. Learn before you leap!' },
  { id: 22, title: 'Avoiding Debt Traps 🚫💳', detail: 'Only borrow what you can repay. Pay your credit card in full each month if possible.' },
];

const LearnScreen = () => {
  const [expandedId, setExpandedId] = useState(null);
  const audioRef = useRef(null);

  const toggleCard = (id) => {
    if (expandedId === id) {
      setExpandedId(null);
    } else {
      setExpandedId(id);
      if (audioRef.current) {
        audioRef.current.currentTime = 0;
        audioRef.current.play();
      }
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold text-center mb-6">📚 Learn About Money & Economics</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {topics.map((topic) => (
          <div
            key={topic.id}
            onClick={() => toggleCard(topic.id)}
            className={`cursor-pointer transition-all duration-500 ease-in-out p-4 rounded-lg shadow-lg bg-white ${
              expandedId === topic.id ? 'col-span-2 row-span-2 transform scale-105 z-10' : ''
            }`}
            style={{ minHeight: expandedId === topic.id ? '50vh' : 'auto' }}
          >
            <h3 className="text-lg font-semibold mb-2">{topic.title}</h3>
            {expandedId === topic.id && (
              <p className="text-sm text-gray-700 whitespace-pre-line">{topic.detail}</p>
            )}
          </div>
        ))}
      </div>
      <audio ref={audioRef} src="/click.mp3" preload="auto" />
    </div>
  );
};

export default LearnScreen;
