import React, { useEffect, useState } from 'react';

const BudgetPlan = ({ onBalanceChange, onAllowanceChange }) => {
  const [incomes, setIncomes] = useState(() => JSON.parse(localStorage.getItem('incomes')) || [{ id: 1, label: 'Income Source', amount: 0 }]);
  const [mustHaves, setMustHaves] = useState(() => JSON.parse(localStorage.getItem('mustHaves')) || [
    { name: 'Student Loans', amount: 120 },
    { name: 'Phone Bill', amount: 40 },
    { name: 'Medicine', amount: 30 },
  ]);
  const [goalAllocations, setGoalAllocations] = useState(() => JSON.parse(localStorage.getItem('goalAllocations')) || [
    { name: 'Car Downpayment', amount: 10 },
    { name: 'Phone', amount: 12 },
  ]);
  const [minSavings, setMinSavings] = useState(() => parseFloat(localStorage.getItem('minSavings')) || 100);

  const totalIncome = incomes.reduce((sum, inc) => sum + (parseFloat(inc.amount) || 0), 0);
  const mustHaveTotal = mustHaves.reduce((sum, item) => sum + (parseFloat(item.amount) || 0), 0);
  const goalTotal = goalAllocations.reduce((sum, item) => sum + (parseFloat(item.amount) || 0), 0);
  const allowance = totalIncome - mustHaveTotal - goalTotal - minSavings;

  useEffect(() => {
    onBalanceChange(totalIncome);
    onAllowanceChange(allowance);
  }, [totalIncome, mustHaveTotal, goalTotal, minSavings]);

  const saveAll = () => {
    localStorage.setItem('incomes', JSON.stringify(incomes));
    localStorage.setItem('mustHaves', JSON.stringify(mustHaves));
    localStorage.setItem('goalAllocations', JSON.stringify(goalAllocations));
    localStorage.setItem('minSavings', minSavings);
  };

  const addIncomeField = () => {
    setIncomes([...incomes, { id: Date.now(), label: 'Income Source', amount: 0 }]);
  };

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">
      <h2 className="text-3xl font-bold text-center">📊 Monthly Budget Planner</h2>

      {/* Income Inputs */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">💼 Income Sources</h3>
        {incomes.map((inc, idx) => (
          <input
            key={inc.id}
            type="number"
            placeholder={`Income #${idx + 1}`}
            value={inc.amount}
            onChange={(e) => {
              const updated = [...incomes];
              updated[idx].amount = e.target.value;
              setIncomes(updated);
            }}
            className="w-full border p-2 mb-2 rounded"
          />
        ))}
        <button onClick={addIncomeField} className="mt-2 text-blue-600 underline">+ Add Another Income</button>
      </div>

      {/* Must-Haves */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">🧾 Must-Haves</h3>
        {mustHaves.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => {
                const updated = [...mustHaves];
                updated[idx].amount = e.target.value;
                setMustHaves(updated);
              }}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      {/* Goals */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">🎯 Goals</h3>
        {goalAllocations.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => {
                const updated = [...goalAllocations];
                updated[idx].amount = e.target.value;
                setGoalAllocations(updated);
              }}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      {/* Savings */}
      <div className="bg-white p-4 shadow rounded">
        <label className="block mb-2 font-semibold">💰 Minimum Savings</label>
        <input
          type="number"
          value={minSavings}
          onChange={(e) => setMinSavings(parseFloat(e.target.value))}
          className="w-full border p-2 rounded"
        />
      </div>

      <button onClick={saveAll} className="bg-green-600 text-white px-6 py-2 rounded block mx-auto">
        💾 Save Budget Plan
      </button>
    </div>
  );
};

export default BudgetPlan;
