import React, { useState, useEffect } from 'react';
import HomeScreen from './components/HomeScreen';
import AddTransaction from './components/AddTransaction';
import BudgetPlan from './components/BudgetPlan';
import SetGoal from './components/SetGoal';
import LearnScreen from './components/LearnScreen';
import NavBar from './components/NavBar';

function App() {
  const [screen, setScreen] = useState('home');
  const [theme, setTheme] = useState('pastel-pink'); // default theme
  const themeToClass = {
    'pastel-pink': 'bg-[#ffe4e1]',    // soft pink
    'beige': 'bg-[#fef6e4]',          // beige
    'sage-light': 'bg-[#cfe3d4]',     // sage green
    'soft-blue': 'bg-[#d6e6f2]',      // NEW: light pastel blue
  };
  const [balance, setBalance] = useState(250);
  const [expenses, setExpenses] = useState([
    { id: 1, category: 'Food', amount: 15 },
    { id: 2, category: 'Transport', amount: 10 },
  ]);
  useEffect(() => 
    {
  const savedExpenses = localStorage.getItem('expenses');
  if (savedExpenses) {
    setExpenses(JSON.parse(savedExpenses));
  }
}, []);

  const [income, setIncome] = useState(0);
  const [mustHaves, setMustHaves] = useState([
    { name: "Student Loans", amount: 120 },
    { name: "Phone Bill", amount: 40 },
  ]);
  const [goalAllocations, setGoalAllocations] = useState([
    { name: "Car Downpayment", amount: 10 },
  ]);
  const [minSavings, setMinSavings] = useState(100);
  const mustHaveTotal = mustHaves.reduce((sum, item) => sum + item.amount, 0);
  const goalTotal = goalAllocations.reduce((sum, item) => sum + item.amount, 0);
  const [allowance, setAllowance] = useState(0);
  const safeAllowance = income - (mustHaveTotal + goalTotal + minSavings);
  const [goals, setGoals] = useState([]);

  return (
    <div className={`min-h-screen ${themeToClass[theme]}`}>
      {/* Theme Selector UI */}
      <div className="flex justify-end p-2 bg-white">
        <select
          className="p-2 border rounded"
          value={theme}
          onChange={(e) => setTheme(e.target.value)}
        >
          <option value="pastel-pink">ðŸŒ¸ Pastel Pink</option>
          <option value="beige">ðŸŸ¤ Beige</option>
          <option value="sage-light">ðŸŒ¿ Sage Light</option>
          <option value="soft-blue">ðŸ’§ Soft Blue</option>
        </select>
      </div>

      {/* Nav + Screens */}
      <NavBar setScreen={setScreen} />
      {screen === 'home' && (<HomeScreen balance={balance} expenses={expenses} allowance={allowance} />)}
      {screen === 'add' && (<AddTransaction setBalance={setBalance} setExpenses={setExpenses} expenses={expenses}/> )}
      {screen === 'budget' && <BudgetPlan setAllowance={setAllowance} />}
      {screen === 'goal' && <SetGoal goals={goals} setGoals={setGoals} />}
      {screen === 'learn' && <LearnScreen />}
    </div>
  );

}

export default App;
