import React, { useState, useEffect } from 'react';

const BudgetPlan = () => {
  const [mustHaves, setMustHaves] = useState(() => {
    const saved = localStorage.getItem('mustHaves');
    return saved ? JSON.parse(saved) : [
      { name: 'Student Loans', amount: 120 },
      { name: 'Phone Bill', amount: 40 },
    ];
  });

  const [goalAllocations, setGoalAllocations] = useState(() => {
    const saved = localStorage.getItem('goalAllocations');
    return saved ? JSON.parse(saved) : [
      { name: 'Car Downpayment', amount: 10 },
    ];
  });

  const [minSavings, setMinSavings] = useState(() => {
    const saved = localStorage.getItem('minSavings');
    return saved ? parseFloat(saved) : 100;
  });

  const [expenses, setExpenses] = useState(() => {
    const saved = localStorage.getItem('expenses');
    return saved ? JSON.parse(saved) : [];
  });

  const [category, setCategory] = useState('');
  const [amount, setAmount] = useState('');

  const mustHaveTotal = mustHaves.reduce((sum, item) => sum + item.amount, 0);
  const goalTotal = goalAllocations.reduce((sum, item) => sum + item.amount, 0);
  const allowance = Math.max(0, 250 - (mustHaveTotal + goalTotal + minSavings));

  useEffect(() => {
    localStorage.setItem('mustHaves', JSON.stringify(mustHaves));
    localStorage.setItem('goalAllocations', JSON.stringify(goalAllocations));
    localStorage.setItem('minSavings', minSavings.toString());
    localStorage.setItem('expenses', JSON.stringify(expenses));
    localStorage.setItem('allowance', allowance.toString());
  }, [mustHaves, goalAllocations, minSavings, expenses, allowance]);

  const handleChange = (setter, index, value) => {
    const updated = [...setter];
    updated[index].amount = parseFloat(value);
    if (setter === mustHaves) setMustHaves(updated);
    else setGoalAllocations(updated);
  };

  const handleAddExpense = () => {
    if (!category || !amount) return;
    const newExpense = {
      id: Date.now(),
      category,
      amount: parseFloat(amount)
    };
    const updated = [...expenses, newExpense];
    setExpenses(updated);
    setCategory('');
    setAmount('');
  };

  return (
    <div className="p-6 max-w-3xl mx-auto space-y-6">
      <h2 className="text-3xl font-bold text-center">📊 Budget Planner</h2>

      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">🧾 Must-Haves</h3>
        {mustHaves.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => handleChange(mustHaves, idx, e.target.value)}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">🎯 Goals</h3>
        {goalAllocations.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => handleChange(goalAllocations, idx, e.target.value)}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      <div className="bg-white p-4 shadow rounded">
        <label className="block mb-2 font-semibold">💰 Minimum Monthly Savings</label>
        <input
          type="number"
          value={minSavings}
          onChange={(e) => setMinSavings(parseFloat(e.target.value))}
          className="w-full border p-2 rounded"
        />
      </div>

      <div className="bg-green-100 p-6 shadow rounded text-center">
        <h3 className="text-xl font-semibold mb-2">🧮 Suggested Monthly Spending Allowance</h3>
        <p className="text-4xl font-bold text-green-800">${allowance.toFixed(2)}</p>
      </div>

      <div className="bg-white p-4 shadow rounded mt-8">
        <h3 className="font-semibold mb-2">➕ Add Transaction</h3>
        <div className="flex flex-col sm:flex-row sm:items-center sm:space-x-4 space-y-2 sm:space-y-0">
          <input
            className="border p-2 rounded w-full sm:w-1/2"
            placeholder="Category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />
          <input
            type="number"
            className="border p-2 rounded w-full sm:w-1/3"
            placeholder="Amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button
            className="bg-green-600 text-white px-4 py-2 rounded"
            onClick={handleAddExpense}
          >
            Add
          </button>
        </div>
      </div>
    </div>
  );
};

export default BudgetPlan;
