import React from 'react';
import { Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Dashboard = ({ balance, allowance, expenses }) => {
  const categories = [...new Set(expenses.map(e => e.category))];

  const pieData = {
    labels: categories,
    datasets: [{
      data: categories.map(cat =>
        expenses.filter(e => e.category === cat)
                .reduce((sum, e) => sum + e.amount, 0)
      ),
      backgroundColor: ['#cfe3d4', '#ffd8be', '#d6e6f2', '#ffe4e1', '#fce1f0', '#f9f7cf'],
      borderWidth: 1,
    }],
  };

  const barData = {
    labels: ['January', 'February', 'March', 'April'],
    datasets: [{
      label: 'Monthly Savings',
      data: [80, 120, 60, 150],
      backgroundColor: '#91a377',
    }],
  };

  const today = new Date().toLocaleDateString();

  return (
    <div className="p-6 max-w-7xl mx-auto space-y-6 font-sans">
      {/* Welcome & Date */}
      <div className="flex flex-col md:flex-row justify-between items-center">
        <h1 className="text-4xl font-bold text-[#800000] font-[Pacifico] mb-4 md:mb-0">
          Hello, Eshita
        </h1>
        <p className="text-gray-600 text-md">{today}</p>
      </div>

      {/* Overview Grid */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white p-4 shadow rounded">
          <h3 className="font-semibold mb-2">üè¶ Checking Account</h3>
          <p className="text-2xl font-bold text-green-700">${(balance * 0.5).toFixed(2)}</p>
        </div>
        <div className="bg-white p-4 shadow rounded">
          <h3 className="font-semibold mb-2">üí≥ Credit/Debit Card</h3>
          <p className="text-2xl font-bold text-blue-600">${(balance * 0.25).toFixed(2)}</p>
        </div>
        <div className="bg-white p-4 shadow rounded">
          <h3 className="font-semibold mb-2">üí∞ Net Cash</h3>
          <p className="text-2xl font-bold text-gray-800">${balance.toFixed(2)}</p>
        </div>
        <div className="bg-white p-4 shadow rounded">
          <h3 className="font-semibold mb-2">üíæ Savings</h3>
          <p className="text-2xl font-bold text-purple-700">${(balance * 0.15).toFixed(2)}</p>
        </div>
        <div className="bg-white p-4 shadow rounded">
          <h3 className="font-semibold mb-2">üìà Investments</h3>
          <p className="text-2xl font-bold text-yellow-600">${(balance * 0.1).toFixed(2)}</p>
        </div>
        <div className="bg-white p-4 shadow rounded">
          <h3 className="font-semibold mb-2">üßæ Monthly Allowance</h3>
          <p className="text-2xl font-bold text-red-700">
            ${allowance > 0 ? allowance.toFixed(2) : '0.00'}
          </p>
          {allowance < 0 && (
            <p className="text-red-500 text-sm mt-1">‚ö†Ô∏è Over budget</p>
          )}
        </div>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-4 shadow rounded">
          <h3 className="font-semibold mb-2">üìä Spending by Category</h3>
          <Pie data={pieData} />
        </div>
        <div className="bg-white p-4 shadow rounded">
          <h3 className="font-semibold mb-2">üìà Monthly Savings</h3>
          <Bar data={barData} />
        </div>
      </div>

      {/* Recent Transactions */}
      <div className="bg-white p-4 shadow rounded">
        <h3 className="text-xl font-semibold mb-3">üìù Recent Transactions</h3>
        <ul className="max-h-64 overflow-y-auto space-y-2">
          {[...expenses].reverse().map((e) => (
            <li key={e.id} className="flex justify-between">
              <span>{e.category}: ${e.amount}</span>
              <span className="text-sm text-gray-500">{e.date || today}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
