import React, { useState } from 'react';

const HomeScreen = () => {
  const [balance, setBalance] = useState(250);
  const [goals, setGoals] = useState([
    { id: 1, name: 'New Shoes', target: 100, saved: 75 },
    { id: 2, name: 'Concert Ticket', target: 60, saved: 60 },
  ]);
  const [expenses, setExpenses] = useState([
    { id: 1, category: 'Food', amount: 15 },
    { id: 2, category: 'Transport', amount: 10 },
  ]);

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Welcome to Budget Bloom ðŸŒ±</h1>

      <div className="bg-green-100 p-4 rounded-lg shadow mb-6">
        <h2 className="text-xl font-semibold">ðŸ’µ Available Balance</h2>
        <p className="text-3xl font-bold">${balance}</p>
      </div>

      <div className="mb-6">
        <h2 className="text-lg font-semibold mb-2">ðŸŽ¯ Goals Progress</h2>
        {goals.map(goal => (
          <div key={goal.id} className="mb-2">
            <p>{goal.name} (${goal.saved}/${goal.target})</p>
            <div className="w-full bg-gray-200 rounded-full h-3">
              <div
                className="bg-sage-dark h-3 rounded-full"
                style={{ width: `${(goal.saved / goal.target) * 100}%` }}
              ></div>
            </div>
          </div>
        ))}
      </div>

      <div>
        <h2 className="text-lg font-semibold mb-2">ðŸ“Š Recent Expenses</h2>
        <ul className="list-disc list-inside">
          {expenses.map(item => (
            <li key={item.id}>
              {item.category}: ${item.amount}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default HomeScreen;
