import React, { useState } from 'react';

const topics = [
  {
    id: 1,
    title: 'Budgeting 💸',
    brief: 'Plan where your money goes before it disappears.',
    detail: 'Budgeting means creating a plan for your income and expenses. It helps you track how much you earn, spend, and save.',
  },
  {
    id: 2,
    title: 'Stocks 📈',
    brief: 'Own a tiny part of a company.',
    detail: 'When you buy a stock, you own a share of a company. Stocks can grow in value, but can also go down — always research and diversify!',
  },
  {
    id: 3,
    title: 'Savings 🏦',
    brief: 'Money you set aside, not spend.',
    detail: 'Savings help you prepare for emergencies and big purchases. Try to save a portion of every dollar you earn.',
  },
  {
    id: 4,
    title: 'Mutual Funds 📊',
    brief: 'A basket of investments managed by pros.',
    detail: 'Mutual funds pool your money with others to invest in a range of stocks or bonds, offering diversification with less work.',
  },
  {
    id: 5,
    title: 'Loans 💳',
    brief: 'Borrowed money — with strings attached.',
    detail: 'Loans help pay for big things now (like college), but must be paid back over time — with interest. Borrow smart.',
  },
];

const LearnScreen = () => {
  const [expandedId, setExpandedId] = useState(null);

  const toggleCard = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-3xl font-bold text-center mb-6">📚 Learn About Money</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {topics.map((topic) => (
          <div
            key={topic.id}
            className="bg-white p-4 rounded-lg shadow hover:shadow-md transition duration-300 cursor-pointer"
            onClick={() => toggleCard(topic.id)}
          >
            <h3 className="text-xl font-semibold mb-2">{topic.title}</h3>
            <p className="text-sm text-gray-700">{topic.brief}</p>
            {expandedId === topic.id && (
              <p className="text-sm text-gray-600 mt-2">{topic.detail}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default LearnScreen;
