import React from 'react';

const LearnScreen = () => {
  const topics = [
    { id: 1, title: 'What is Compound Interest?', desc: 'Itâ€™s how your money grows over time, even on the interest youâ€™ve already earned!' },
    { id: 2, title: 'Mutual Funds vs Stocks', desc: 'Mutual funds pool your money. Stocks are single-company investments.' },
    { id: 3, title: 'High-Yield Savings', desc: 'A savings account that earns more interest than normal. Great for emergency funds!' }
  ];

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-xl font-bold mb-4">ðŸ“š Learn About Money</h2>
      {topics.map(topic => (
        <div key={topic.id} className="bg-white p-4 shadow rounded mb-4">
          <h3 className="font-semibold">{topic.title}</h3>
          <p>{topic.desc}</p>
        </div>
      ))}
    </div>
  );
};

export default LearnScreen;
