import React, { useState } from 'react';

const AddTransaction = ({ setBalance, setExpenses, expenses }) => {
  const [category, setCategory] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newExpense = {
      id: Date.now(),
      category,
      amount: parseFloat(amount),
    };
    setExpenses(prev => [...prev, newExpense]);
    setBalance(prev => prev - parseFloat(amount));
    setCategory('');
    setAmount('');
  };

  return (
    <div className="p-6 max-w-xl mx-auto space-y-6">
      <h2 className="text-2xl font-bold mb-4">ðŸŒ¿ Add a Transaction</h2>

      {/* Form */}
      <form onSubmit={handleSubmit} className="bg-white p-4 shadow rounded space-y-4">
        <input
          type="text"
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="w-full border p-2 rounded"
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="w-full border p-2 rounded"
          required
        />
        <button type="submit" className="bg-sage text-white px-4 py-2 rounded hover:bg-sage-dark">
          Add Transaction
        </button>
      </form>

      {/* Transaction List */}
      <div>
        <h3 className="text-lg font-semibold mb-2">ðŸ“œ Recent Transactions</h3>
        <ul className="space-y-2">
          {expenses.slice(-5).reverse().map((t) => (
            <li key={t.id} className="bg-white shadow p-3 rounded flex justify-between items-center">
              <span className="font-medium">{t.category}</span>
              <span className="text-right">${t.amount.toFixed(2)}</span>
            </li>
          ))}
        </ul>
        <button className="mt-2 text-sm text-sage hover:underline">Show all</button>
      </div>
    </div>
  );
};

export default AddTransaction;
