import React from 'react';
import { Pie, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const HomeScreen = ({ balance, expenses, allowance }) => {
  const categories = [...new Set(expenses.map(e => e.category))];
  const date = new Date().toLocaleDateString(undefined, { weekday: 'long', month: 'long', day: 'numeric' });

  const pieData = {
    labels: categories,
    datasets: [{
      data: categories.map(cat =>
        expenses.filter(e => e.category === cat).reduce((sum, e) => sum + e.amount, 0)
      ),
      backgroundColor: ['#cfe3d4', '#ffd8be', '#d6e6f2', '#ffe4e1', '#fce1f0', '#f9f7cf'],
      borderWidth: 1,
    }],
  };

  const barData = {
    labels: ['January', 'February', 'March', 'April'],
    datasets: [{
      label: 'Monthly Savings',
      data: [80, 120, 60, 150],
      backgroundColor: '#91a377',
    }],
  };

  return (
    <div className="p-6 max-w-6xl mx-auto space-y-6">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-5xl font-[Pacifico] text-red-700">hello eshita</h1>
        <p className="text-gray-600 text-sm">{date}</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-white p-6 shadow rounded text-center">
          <h2 className="text-lg font-semibold mb-2">💰 Available Balance</h2>
          <p className="text-4xl font-bold text-green-800">${balance}</p>
        </div>

        <div className="bg-white p-6 shadow rounded text-center">
          <h2 className="text-lg font-semibold mb-2">🧾 Suggested Monthly Allowance</h2>
          <p className="text-3xl font-bold text-blue-700">
            ${allowance > 0 ? allowance.toFixed(2) : '0.00'}
          </p>
        </div>

        <div className="bg-white p-6 shadow rounded text-center">
          <h2 className="text-lg font-semibold mb-2">🏦 Net Cash</h2>
          <p className="text-2xl text-black">${(balance - expenses.reduce((s, e) => s + e.amount, 0)).toFixed(2)}</p>
        </div>

        <div className="bg-white p-4 shadow rounded col-span-2">
          <h2 className="text-lg font-semibold mb-2">📊 Spending by Category</h2>
          {categories.length > 0 ? <Pie data={pieData} /> : <p>No transactions yet</p>}
        </div>

        <div className="bg-white p-4 shadow rounded col-span-2">
          <h2 className="text-lg font-semibold mb-2">📈 Monthly Savings</h2>
          <Bar data={barData} />
        </div>

        <div className="bg-white p-4 shadow rounded col-span-full">
          <h2 className="text-lg font-semibold mb-2">📝 Recent Activity</h2>
          <ul className="list-disc list-inside max-h-40 overflow-y-auto">
            {[...expenses].reverse().map((e, i) => (
              <li key={i}>{e.category}: ${e.amount}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default HomeScreen;
