import React, { useState } from 'react';

const BudgetPlan = () => {
  const [income, setIncome] = useState(0);
  const [mustHaves, setMustHaves] = useState([
    { name: 'Student Loans', amount: 120 },
    { name: 'Phone Bill', amount: 40 },
    { name: 'Medicine', amount: 30 },
  ]);
  const [goalAllocations, setGoalAllocations] = useState([
    { name: 'Car Downpayment', amount: 10 },
  ]);
  const [minSavings, setMinSavings] = useState(100);

  const mustHaveTotal = mustHaves.reduce((sum, item) => sum + item.amount, 0);
  const goalTotal = goalAllocations.reduce((sum, item) => sum + item.amount, 0);
  const allowance = income - (mustHaveTotal + goalTotal + minSavings);

  const handleChangeMustHave = (index, value) => {
    const updated = [...mustHaves];
    updated[index].amount = parseFloat(value);
    setMustHaves(updated);
  };

  const handleChangeGoal = (index, value) => {
    const updated = [...goalAllocations];
    updated[index].amount = parseFloat(value);
    setGoalAllocations(updated);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto space-y-6">
      <h2 className="text-3xl font-bold text-center">📊 Monthly Budget Planner</h2>

      <div className="bg-white p-4 shadow rounded">
        <label className="block mb-2 font-semibold">💼 Monthly Income</label>
        <input
          type="number"
          value={income}
          onChange={(e) => setIncome(parseFloat(e.target.value))}
          className="w-full border p-2 rounded"
          placeholder="Enter your monthly income"
        />
      </div>

      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">🧾 Must-Have Expenses</h3>
        {mustHaves.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => handleChangeMustHave(idx, e.target.value)}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      <div className="bg-white p-4 shadow rounded">
        <h3 className="font-semibold mb-2">🎯 Goal-Based Allocations</h3>
        {goalAllocations.map((item, idx) => (
          <div key={idx} className="flex justify-between mb-2">
            <span>{item.name}</span>
            <input
              type="number"
              value={item.amount}
              onChange={(e) => handleChangeGoal(idx, e.target.value)}
              className="w-24 border p-1 rounded text-right"
            />
          </div>
        ))}
      </div>

      <div className="bg-white p-4 shadow rounded">
        <label className="block mb-2 font-semibold">💰 Minimum Monthly Savings</label>
        <input
          type="number"
          value={minSavings}
          onChange={(e) => setMinSavings(parseFloat(e.target.value))}
          className="w-full border p-2 rounded"
        />
      </div>

      <div className="bg-sage-light p-6 shadow rounded text-center">
        <h3 className="text-xl font-semibold mb-2">🧮 Suggested Monthly Spending Allowance</h3>
        <p className="text-4xl font-bold text-green-800">
          ${allowance > 0 ? allowance.toFixed(2) : '0.00'}
        </p>
        {allowance < 0 && (
          <p className="text-red-600 mt-2">⚠️ You're over budget. Consider reducing expenses or increasing income.</p>
        )}
      </div>
    </div>
  );
};

export default BudgetPlan;
